mapper.select:
SELECT gu.*,gl1.* ,gl2.goodsId as ugoodsId ,gl2.goodsPrice as ugoodsPrice ,gl2.dateManufacture as udateManufacture ,gl2.goodsName as ugoodsName ,gl2.imageUrl as uimageUrl ,gl2.inventory as uinventory ,gl2.salesVolume as usalesVolume ,gl2.isShelves as uisShelves ,gl2.weight as uweight ,gl2.effectiveTime as ueffectiveTime ,gl2.hotGoods as uhotGoods
FROM goodslistUpgrade AS gu INNER JOIN `goodslist` AS gl1 ON gu.goodsId=gl1.goodsId INNER JOIN `goodslist` AS gl2 ON gu.UpgradeGoodsId=gl2.goodsId where 1=1
and gu.`goodsId`=#{goodsId}
and gu.`UpgradeGoodsId`=#{UpgradeGoodsId}
